import React from 'react'
import Editor from './Editor'

class AddExplanation extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loggedInUser: '@barackobama',
      concept: 'emergence'
    }
  }
  render() {
    return (
      <div>
        <div onClick={this.props.handleAnswerClick} className="closeSection"><i className="material-icons">close_rounded</i></div>
        <h1>Hi {this.state.loggedInUser}</h1>
        <p>What do you think is the most fun and delightful way to explain <span className="concept-name">{this.state.concept}</span>?</p>
        <Editor placeholder={`Write something short, sweet and wonderfully wacky that explains ${this.state.concept}.`} />
        <div className="buttonBar">
          <div className="submit-button button">Submit</div>
          <div className="submit-and-tweet-button button">This is my best work. Submit and tweet.</div>
        </div>
      <style jsx>{`
        .closeSection {
          position: relative;
          float: right;
          width: 30px;
        }
        h1 {
          margin-bottom: 0;
        }
        p {
          margin-top: 10px;
          margin-bottom: 5px;
          color: #a9a9a9;
        }
        .button {
          display: inline-block;
          padding: 10px 30px 10px 30px;
          border-radius: 20px;
          margin: 0;
          color: white;
        }
        .submit-button {
          background: #A3D066;
        }
        .submit-and-tweet-button {
          background: #6EC3E9;
        }
      `}</style>
      </div>
    )
  }
}

export default AddExplanation
