import parse from 'html-react-parser';

function ExplanationsSection(props) {
  return (
    <div className="explanationsSection">
     {
       props.explanations.map((e, index) => {
         return (
           <div className="explanation" id={`explanation-${index}`}>
             <div className="attribution">
               <img className="attributionAvatar" src="https://pbs.twimg.com/profile_images/822547732376207360/5g0FC8XX.jpg" />
               <p className="attributionUsername">{e.author}</p>
             </div>
             <div className="explanationMeat">
               {parse(e.explanation)}
             </div>
             <div className="explanationActionBar">
               <div className="explanationAction">
                 {e.score > 0 && <p className="score">+ {e.score}</p>}
                 <img src="/images/get-it-face.png" />
               </div>
               <div className="explanationAction">
                 <img src="/images/dont-get-it-face.png"/>
               </div>
             </div>
           </div>
         )
       })
     }
      <style jsx>{`
        .explanation {
          border-bottom: 1px solid #ddd;
          margin-bottom: 20px;
        }
        .explanation:last-child {
          border: none;
        }
        .attributionAvatar {
          width: 30px;
          border-radius: 50px;
          display: inline-block;
          position: relative;
          top: 5px;
        }
        .attributionUsername {
          display: inline-block;
          margin-left: 10px;
        }
        .explanationMeat {
          padding-bottom: 10px;
        }
        .explanationActionBar {
          padding-top: 75px;
        }
        .explanationAction {
          display: inline-block;
          transition:0.3s;
        }
        .explanationAction:nth-of-type(2) {
          margin-left: 35px;
        }
        .explanationAction img {
          display: inline-block;
          width: 50px;
          position: relative;
          top: 5px;
          margin-right: 5px;
        }
        .explanationAction p {
          display: inline-block;
        }
        /*.explanationAction:hover {
          transform: scale(1.1);
          transform-origin: center;
          filter: brightness(115%);
          cursor: pointer;
        }
        .explanationAction:active:first-child {
          transform: rotate(180deg)
        }*/
        .speech-bubble {
          position: relative;
        	background: #e8e8e8;
        	border-radius: .4em;
          padding: 10px;
          z-index: 1;
          bottom: 75px;
          right: 15px;
        }

        .speech-bubble:after {
          content: '';
          position: absolute;
          bottom: 10px;
          left: 20%;
          width: 0;
          height: 0;
          border: 28px solid transparent;
          border-top-color: #e8e8e8;
          border-bottom: 0;
          border-left: 0;
          margin-left: -19px;
          margin-bottom: -28px;
        }
        .score {
          margin-right: 10px;
          color: #bbb;
        }

      `}</style>
    </div>
  )
}

export default ExplanationsSection
