import React from 'react'
import parse from 'html-react-parser';
import { addVote } from './Firebase'
import Link from 'next/link'

class ExplanationsSection extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loggedInUser: '@barackobama'
    }
    this.handleVote = this.handleVote.bind(this)
  }
  async handleVote(vote, explanationID) {
    await addVote(vote, this.state.loggedInUser, explanationID)
    // await this.props.parentHandleVote()
    await this.props.handleUdatingDisplayedScores()
  }
  render() {
    return (
      <div className="explanationsSection">
         <div className="explanation" key={`explanation-${this.props.explanations[0].id}`}>
          {
            this.props.profilepage &&
            <Link href={`/explainthis/${this.props.explanations[0].concept}`}>
              <div className="link conceptName">{`${this.props.explanations[0].author} explains ${this.props.explanations[0].concept}`}</div>
            </Link>
          }
          {
            !this.props.profilepage &&
            <Link href={`/explainer/${this.props.explanations[0].author}`}>
              <div className="attribution link">
                <img className="attributionAvatar" src="https://pbs.twimg.com/profile_images/822547732376207360/5g0FC8XX.jpg" />
                <p className="attributionUsername">{this.props.explanations[0].author}</p>
              </div>
            </Link>
          }
           <div className="explanationMeat">
             {parse(e.explanation)}
           </div>
           <div className="explanationActionBar">
             <div className="explanationAction" onClick={() => this.handleVote(1, e.id)}>
               <img src="/images/get-it-face.png" />
               {this.props.explanations[0].score > 0 && <p className="score">+ {this.props.explanations[0].score}</p>}
             </div>
             <div className="explanationAction" onClick={() => this.handleVote(-1, e.id)} >
               <img src="/images/dont-get-it-face.png"/>
             </div>
           </div>
         </div>
       }
        <style jsx>{`
          ExplanationsSection {
            position: relative;
            bottom: 20px;
          }
          .explanation {
            border-bottom: 1px solid #ddd;
            margin-bottom: 20px;
            position: relative;
            bottom: 20px;
          }
          .explanation:last-child {
            border: none;
          }
          .attributionAvatar {
            width: 30px;
            border-radius: 50px;
            display: inline-block;
            position: relative;
            top: 5px;
          }
          .attributionUsername {
            display: inline-block;
            margin-left: 10px;
          }
          .explanationMeat {
            padding-bottom: 5px;
            font-size: 30px;
          }
          .explanationActionBar {
            padding-bottom: 20px;
          }
          .explanationAction {
            display: inline-block;
            transition:0.3s;
          }
          .explanationAction:nth-of-type(2) {
            margin-left: 35px;
          }
          .explanationAction img {
            display: inline-block;
            width: 24px;
            position: relative;
            top: 5px;
            margin-right: 5px;
          }
          .explanationAction p {
            display: inline-block;
          }
          explanationAction:hover {
            transform: scale(1.1);
            transform-origin: center;
            filter: brightness(115%);
            cursor: pointer;
          }
          .explanationAction:active:first-child {
            transform: rotate(180deg)
          }
          .score {
            margin-right: 10px;
            color: #bbb;
          }
          .conceptName {
            font-weight: bold;
            font-size: 24px;
          }
          .link {
            cursor:pointer;
          }
          .link:hover {
            filter: brightness(115%);
          }
          .link:active {
            filter: brightness(125%);
          }
        `}</style>
      </div>
    )
  }
}

export default ExplanationsSection
