import React from 'react'
import Header from '../../components/Header'
import AppContainer from '../../components/AppContainer';
import ExplanationsSection from '../../components/ExplanationsSection'
import { getUsersExplanations, getUserPoints } from '../../components/Firebase'
import Loading from '../../components/Loading'
import { useRouter } from 'next/router';

class Explainer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      data: [],
      userPoints: {}
    }
  }
  async componentDidMount() {
    this.setState({
      data: await getUsersExplanations('@barackobama'),
      userPoints: await getUserPoints('@barackobama')
    })
    const router = useRouter();
  }
  render() {
    {
      if (!this.state.data.explanations || !this.state.userPoints.points || !router) {
        return <Loading />
      } else {
        return (
          <div>
            <Header />
            <AppContainer>
              <div className="header">
                <div className="column">
                  <img className="avatar" src="https://pbs.twimg.com/profile_images/822547732376207360/5g0FC8XX.jpg" />
                </div>
                <div className="column">
                  <h1 className="username">{router.query.id}</h1>
                  <div className="header-stats">
                    <div className="header-stat-item">
                      <i className="material-icons">emoji_events_rounded</i>
                      <p>Leaderboard Place • #15</p>
                    </div>
                    <div className="header-stat-item">
                      <i className="material-icons">emoji_people_rounded</i>
                      <p>Contributed Explanations • {this.state.userPoints.numContributedExplanations}</p>
                    </div>
                    <div className="header-stat-item">
                      <i className="material-icons">emoji_flags_rounded</i>
                      <p>Points • {this.state.userPoints.points}</p>
                    </div>
                  </div>
                </div>
              </div>
              <div className="explanationsSection">
                <p>All of @barackobama's explanations:</p>
                <ExplanationsSection profilepage={true} explanations={this.state.data.explanations} />
              </div>
            </AppContainer>
            <style jsx>{`
              .avatar {
                border-radius: 100px;
                width: 100px;
              }
              .column {
                flex: 50%;
                display: inline-block;
              }
              .header-stat-item {
                display: inline-block;
              }
              .header-stat-item p {
                display: inline-block;
                margin-left: 5px;
              }
              .header-stat-item i {
                width: 24px;
                position: relative;
                top: 5px;
              }
              .header-stat-item:not(:first-of-type) {
                margin-left: 35px;
              }
              .header {
                border-bottom: 2px solid #ddd;
              }
              .username {
                margin-bottom: 5px;
                margin-top: 0px;
              }
              .column:not(:first-of-type) {
                margin-left: 15px;
                position: relative;
                bottom: 5px;
              }
              .explanationsSection p {
                padding-bottom: 10px;
                border-bottom: 1px solid #ddd;
                font-weight: bold;
              }
            `}</style>
          </div>
        )
      }
    }
  }
}

export default Explainer
