const AppContainer = props => (
  <div className="appContainerStyle">
    {props.children}
  <style jsx>{`
    .appContainerStyle {
      height: -webkit-fill-available;
      padding: 20px;
      padding-left: 40px;
      border-radius: 30px;
      background-color: white;
      height: 700px;
      display: flex;
      flex-direction: column;
      flex-basis: 100%;
      flex: 1;
      margin-top: 20px;
      width: 130%;
    }
    @media (max-width: 600px) {
      .appContainerStyle {
        width: 90%;
        margin: 0 auto;
      }
    }
  `}</style>
  </div>
);

export default AppContainer;
