import React from 'react'
import App from 'next/app'
import Head from 'next/head';
import '../components/Firebase'
import { loginWithTwitter } from '../components/Firebase'
import '../components/Twitter'

class MyApp extends App {
  constructor(props) {
    super(props)
    this.state = {
      authenticated: false
    }
    this.updateAuth = this.updateAuth.bind(this)
  }
  async updateAuth() {
    await loginWithTwitter()
    this.state = {
      authenticated: !this.state.authenticated
    }
  }
  render() {
    const { Component, pageProps } = this.props
    return (
      <div className="wrapper">
      <Head>
        <link href="https://fonts.googleapis.com/css?family=Nunito:700,800,900&display=swap" rel="stylesheet" />
        <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
        <link rel="stylesheet" href="//cdn.quilljs.com/1.2.6/quill.snow.css" />
      </Head>
      <Component  updateAuth={this.updateAuth} authenticated={this.state.authenticated} {...pageProps} />
      <style jsx global>{`
        .wrapper {
          margin: 0;
          font-family: 'Nunito', sans-serif;
        }
      `}</style>
      </div>
    )
  }
}

MyApp.getInitialProps = async (appContext) => {
  // calls page's `getInitialProps` and fills `appProps.pageProps`
  const appProps = await App.getInitialProps(appContext);

  return { ...appProps }
}

export default MyApp
